package example;
/**
 * 문자 변수에 대한 연습입니다.
 * 
 */
public class String_Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//문자열 변수
		String first;
		
		first = "coding";
		
		System.out.println(first);
		
		//변수 여러개 선언
		String a, b;
		a = "coding";
		b = "everybody";
		
		System.out.println(a+b);
		
	}

}
