package example;

/**
 * 정수와 실수의 변수에 대한 연습입니다.
 */

public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//정수 변수 선언
		int a;
		a=1;
		System.out.println(a+1); //2
		
		a=2;
		System.out.println(a+1); //3
		
		//실수 => 실수는 소수점
		double b;
		b = 1.1;
		
		System.out.println(a+b);
		
	}

}
